package nts.uk.ctx.sys.env.dom.mailnoticeset.dto;

/**
 * The Interface PersonContactGetMemento.
 */
public interface PersonContactGetMemento {

	/**
	 * Gets the person id.
	 *
	 * @return the person id
	 */
	public String getPersonId();

	/**
	 * Gets the mail address.
	 *
	 * @return the mail address
	 */
	public String getMailAddress();

	/**
	 * Gets the mobile mail address.
	 *
	 * @return the mobile mail address
	 */
	public String getMobileMailAddress();

	/**
	 * Gets the cell phone no.
	 *
	 * @return the cell phone no
	 */
	public String getCellPhoneNo();

}
